import streamlit as stfrom data_fetcher import fetch_movie_datafrom quiz_generator import generate_quizfrom score_calculator import calculate_scoredef start_quiz():    movie_name = st.text_input("Enter the name of a movie:")    if movie_name:        movie_data = fetch_movie_data(movie_name)        question, options = generate_quiz(movie_data)        if question:            st.write(question)            for i, option in enumerate(options, 1):                st.write(f"{i}. {option}")            user_answer = st.number_input("Enter your answer (1-4):", min_value=1, max_value=4)            correct_answer = options.index(movie_data['year']) + 1  # Correct answer is the year of release            score = calculate_score(user_answer, correct_answer)            st.write(f"Your score: {score}")        else:            st.write("Movie not found.")    else:        st.write("Please enter a movie name.")if __name__ == "__main__":    start_quiz()