from data_fetcher import fetch_movie_datafrom quiz_generator import generate_quizfrom score_calculator import calculate_scoredef start_quiz():    movie_name = input("Enter the name of a movie: ")    movie_data = fetch_movie_data(movie_name)    question, options = generate_quiz(movie_data)    if question:        print(question)        for i, option in enumerate(options, 1):            print(f"{i}. {option}")        user_answer = int(input("Enter your answer (1-4): "))        correct_answer = options.index(movie_data['year']) + 1  # Correct answer is the year of release        score = calculate_score(user_answer, correct_answer)        print(f"Your score: {score}")    else:        print("Movie not found.")if __name__ == "__main__":    start_quiz()